testcase.input:
public class A {
    public void foo(int a, int b) {
        switch(a) {
            case 1:
                break;
        }
    }
}

testcase.result:
public class A {
    public void foo(int a, int b) {
        switch(a) {
            case 1:
                break;
            default :
                break;
        }
    }
}
